}
@font-face{font-family:'Akkurat Mono';src:url("~assets/fonts/Akkurat-Mono.woff") format("woff")
}
html{font-family:'Akkurat Light';color:#fff;font-size:14px;overflow-x:hidden;background-color:#232323;overflow-y:auto
}
body{overflow-x:hidden;height:100%;margin:0
}
#main-content{position:relative;z-index:1
}
.font--stylized{font-family:'Akkurat Mono'
}
h1{line-height:2.3;font-size:17px
}
a{color:#fff !important;text-decoration:none !important
}
.page-leave-active,.page-enter-active{-webkit-transition:opacity 0s .8s, -webkit-transform .8s .2s;transition:opacity 0s .8s, -webkit-transform .8s .2s;-o-transition:transform .8s .2s, opacity 0s .8s;transition:transform .8s .2s, opacity 0s .8s;transition:transform .8s .2s, opacity 0s .8s, -webkit-transform .8s .2s
}
.link--stylized:hover{color:#F8E71C !important
}
